<?phpnamespace App\Helpers;class SeoHelper{    /**     * 生成网站结构化数据     */    public static function generateWebsiteSchema($siteData = [])    {        $siteName = $siteData['site_name'] ?? 'Job Chain';        $siteDescription = $siteData['site_description'] ?? 'Connecting Top Talent with the Best Job Opportunities';        $siteUrl = url('/');                return [            '@context' => 'https://schema.org',            '@type' => 'WebSite',            'name' => $siteName,            'description' => $siteDescription,            'url' => $siteUrl,            'potentialAction' => [                '@type' => 'SearchAction',                'target' => $siteUrl . '/jobs?keyword={search_term_string}',                'query-input' => 'required name=search_term_string'            ]        ];    }    /**     * 生成组织结构化数据     */    public static function generateOrganizationSchema($siteData = [])    {        $siteName = $siteData['site_name'] ?? 'Job Chain';        $siteUrl = url('/');                return [            '@context' => 'https://schema.org',            '@type' => 'Organization',            'name' => $siteName,            'url' => $siteUrl,            'logo' => asset('assets/images/logo.svg'),            'sameAs' => [                '#', // 可以添加社交媒体链接            ],            'contactPoint' => [                '@type' => 'ContactPoint',                'telephone' => '+86-10-1234-5678',                'contactType' => 'customer service',                'email' => 'contact@myjobportal.com'            ]        ];    }    /**     * 生成职位发布结构化数据     */    public static function generateJobPostingSchema($job)    {        $schema = [            '@context' => 'https://schema.org',            '@type' => 'JobPosting',            'title' => $job['name'] ?? $job['title'] ?? '',            'description' => strip_tags($job['description'] ?? ''),            'datePosted' => $job['created_at'] ?? date('c'),            'validThrough' => $job['expires_at'] ?? date('c', strtotime('+30 days')),            'employmentType' => $job['employment_type'] ?? 'FULL_TIME',            'hiringOrganization' => [                '@type' => 'Organization',                'name' => $job['company_name'] ?? '',                'logo' => $job['company_logo'] ?? asset('assets/images/company-logos/company1.svg')            ],            'jobLocation' => [                '@type' => 'Place',                'address' => [                    '@type' => 'PostalAddress',                    'addressLocality' => $job['location'] ?? '',                    'addressCountry' => 'CN'                ]            ],            'baseSalary' => [                '@type' => 'MonetaryAmount',                'currency' => $job['salary_currency'] ?? 'CNY',                'value' => [                    '@type' => 'QuantitativeValue',                    'minValue' => $job['salary_min'] ?? 0,                    'maxValue' => $job['salary_max'] ?? 0,                    'unitText' => 'MONTH'                ]            ],            'url' => route('jobs.show', $job['slug'] ?? $job['id'])        ];        // 清理空值        return array_filter($schema, function($value) {            return !empty($value) && $value !== '';        });    }    /**     * 生成面包屑导航结构化数据     */    public static function generateBreadcrumbSchema($items)    {        $breadcrumbList = [            '@context' => 'https://schema.org',            '@type' => 'BreadcrumbList',            'itemListElement' => []        ];        $position = 1;        foreach ($items as $item) {            $breadcrumbList['itemListElement'][] = [                '@type' => 'ListItem',                'position' => $position,                'name' => $item['name'],                'item' => $item['url'] ?? ''            ];            $position++;        }        return $breadcrumbList;    }    /**     * 生成 FAQ 结构化数据     */    public static function generateFAQSchema($questions)    {        $faqSchema = [            '@context' => 'https://schema.org',            '@type' => 'FAQPage',            'mainEntity' => []        ];        foreach ($questions as $question) {            $faqSchema['mainEntity'][] = [                '@type' => 'Question',                'name' => $question['question'],                'acceptedAnswer' => [                    '@type' => 'Answer',                    'text' => $question['answer']                ]            ];        }        return $faqSchema;    }    /**     * 生成 SEO 元数据     */    public static function generateMetaData($data = [])    {        $defaults = [            'title' => 'Job Chain - Connecting outstanding talent with the best job opportunities',            'description' => 'A professional recruitment platform providing the latest job information, helping companies find suitable talent and helping job seekers find their ideal jobs.',            'keywords' => 'Recruitment, Job Seeking, Career Opportunities, Job Search',            'canonical' => url()->current(),            'og_type' => 'website',            'og_image' => asset('assets/images/logo.svg'),            'twitter_card' => 'summary'        ];        return array_merge($defaults, $data);    }    /**     * 生成页面标题     */    public static function generateTitle($pageTitle, $siteName = null)    {        $siteName = $siteName ?? 'Job Chain';        return $pageTitle . ' - ' . $siteName;    }}